mapper.select:
/* Mapper - listStudent */ SELECT no, sd_num, sd_name, sd_id, sd_passwd, s_num, sd_birthday, sd_phone, sd_address, sd_email, sd_date FROM student
sd_name Like '%'||#{sd_name}||'%'
ORDER BY no ASC

mapper.insert:
/* Mapper - insertSubject */
SELECT student_seq.nextval FROM dual
INSERT INTO student(no, sd_num, sd_name, sd_id, sd_passwd, s_num, sd_birthday, sd_phone, sd_address, sd_email) VALUES(#{no}, #{sd_num}, #{sd_name}, #{sd_id}, #{sd_passwd}, #{s_num}, #{sd_birthday}, #{sd_phone}, #{sd_address}, #{sd_email})

mapper.select:
/* Mapper - selectStudent */
SELECT no, sd_num, sd_name, sd_id, sd_passwd, s_num, sd_birthday, sd_phone, sd_address, sd_email, sd_date
FROM student
WHERE no = ${no}

/* Mapper - selectSubjectNum */
SELECT s_num, s_name
FROM subject
