mapper.select:
SELECT userId, userPw, userName
FROM spring_member
WHERE userId = #{userId}

/* 로그인 처리 쿼리 */
SELECT userId, userPw, userName
FROM spring_member
WHERE userId = #{userId} and userPw = #{userPw}

mapper.update:
UPDATE login_history
SET retry = #{retry}, lastFailedLogin = #{lastFailedLogin}, lastSuccessedLogin = #{lastSuccessedLogin}, clientIP = #{clientIP, jdbcType=VARCHAR}
WHERE idx = #{idx} and userId = #{userId}

mapper.select:
SELECT idx, userId, retry, lastFailedLogin, lastSuccessedLogin, clientIP
FROM login_history
WHERE userId = #{userId}
