mapper.select:
SELECT userId, salt
FROM security
WHERE userId = #{userId}

SELECT idx, userId, userPw, userName, pinno, email, phone
FROM spring_member
WHERE userId = #{userId}

mapper.insert:
/* member - memberInsert */
SELECT spring_member_seq.nextval FROM dual
INSERT INTO spring_member(idx, userId, userPw, userName, pinno, email, phone) VALUES(#{idx}, #{userId}, #{userPw}, #{userName}, #{pinno}, #{email}, #{phone})

mapper.update:
UPDATE spring_member
SET 
		userPw = #{userPw},

		phone = #{phone}, email = #{email}
WHERE userIid = #{userId}
