mapper.insert:
/* Reply - replyInsert */
SELECT spring_reply_seq.nextval FROM dual
INSERT INTO spring_reply(r_num, b_num, r_name, r_content, r_pwd) VALUES(#{r_num}, #{b_num}, #{r_name}, #{r_content}, #{r_pwd})

mapper.select:
/* Reply - pwdConfirm */
SELECT NVL((SELECT 1
			FROM spring_reply
			WHERE r_num = #{r_num} AND r_pwd = #{r_pwd}
			), 0) AS state
FROM dual

mapper.update:
/* Reply - replyUpdate */
UPDATE spring_reply
SET r_content = #{r_content}, r_date = SYSDATE
WHERE r_num = #{r_num}

mapper.delete:
/* Reply - replyDelete */
DELETE FROM spring_reply
WHERE r_num = #{r_num}
