mapper.select:
/* Board - boardList */
select b_num, b_name, b_title,
to_char(b_date, 'YYYY-MM-DD HH24:MI:SS') as b_date
from (
	select list.*, rownum as rnum
	from(



				order by b_num


				order by b_date




				ASC


				DESC


	) list
)



			rnum between #{start_row} and #{end_row}

/* Board - boardListCnt */
select nvl(count(1), 0) from
(	select list.*, rownum as rnum
	from(

		order by b_num desc
	)list
)

select nvl((
	select 1 from spring_board
	where b_num = #{b_num}
	and b_pwd = #{b_pwd}
), 0) as state
from dual

mapper.update:
UPDATE spring_board SET b_title = #{b_title}, b_content = #{b_content}, b_date = sysdate,
b_file = #{b_file},
b_pwd = #{b_pwd}
WHERE b_num = #{b_num}
